jQuery.sap.declare("GlAccountsWiki.GlAccountsWiki.util.formatter");GlAccountsWiki.GlAccountsWiki.util.formatter={dateFormatter:function(t){if(!t){return""}var r=new Date(t);var e=["Jan","Feb","Mar","Apr","May","Jun","Jul","Aug","Sep","Oct","Nov","Dec"];var i=r.getFullYear().toString();var a=e[r.getMonth()];var n=r.getDate();if(n<10){n="0"+n.toString()}var o=a+" "+n+", "+i;var u="AM";var c=r.getHours();if(c>=12){u="PM";if(c>12){c=c-12}}if(c<10){c="0"+c}var g=r.getMinutes();if(g<10){g="0"+g}var v=r.getSeconds();if(v<10){v="0"+v}var f=c.toString()+":"+g.toString()+":"+v.toString()+" "+u;return o+" "+f}};